#!/usr/bin/env python3


def find_first(seq: str, size=4) -> int:
    for idx in range(0, len(seq) - size):
        if len(set(seq[idx : idx + size])) == size:
            print(seq[idx : idx + size])
            return idx + size
    return 0


if __name__ == "__main__":
    print(
        find_first(
            "",
            14,
        )
    )
